import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BowlerTable() {
  const [bowlers, setBowlers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBowlers = async () => {
      try {
        const response = await axios.get('/api/bowlers');
        setBowlers(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching bowler data. Please try again later.');
        setLoading(false);
        console.error('Error fetching data:', err);
      }
    };

    fetchBowlers();
  }, []);

  if (loading) {
    return <div>Loading bowler data...</div>;
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>;
  }

  return (
    <div className="table-responsive">
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Team</th>
            <th>Address</th>
            <th>City</th>
            <th>State</th>
            <th>Zip</th>
            <th>Phone</th>
          </tr>
        </thead>
        <tbody>
          {bowlers.map((bowler) => (
            <tr key={bowler.bowlerId}>
              <td>
                {bowler.bowlerFirstName}{' '}
                {bowler.bowlerMiddleInit ? bowler.bowlerMiddleInit + '. ' : ''}
                {bowler.bowlerLastName}
              </td>
              <td>{bowler.team ? bowler.team.teamName : 'N/A'}</td>
              <td>{bowler.bowlerAddress}</td>
              <td>{bowler.bowlerCity}</td>
              <td>{bowler.bowlerState}</td>
              <td>{bowler.bowlerZip}</td>
              <td>{bowler.bowlerPhoneNumber}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {bowlers.length === 0 && (
        <div className="alert alert-info">No bowlers found.</div>
      )}
    </div>
  );
}

export default BowlerTable;
